import { NextResponse } from 'next/server';

export const dynamic = 'force-dynamic';

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const text = searchParams.get('text');

    if (!text) {
      return NextResponse.json(
        { error: 'Text parameter is required' },
        { status: 400 }
      );
    }

    // In a real implementation, this would return actual audio data
    // For now, we'll return a mock response with a data URL for a silent audio file
    
    // This is a base64-encoded 1-second silent MP3 file
    const silentAudioBase64 = '';
    
    // Return a data URL for the silent audio
    return new Response(`data:audio/mp3;base64,${silentAudioBase64}`, {
      headers: {
        'Content-Type': 'text/plain',
      },
    });
    
  } catch (error) {
    console.error('Mock Audio API error:', error);
    return NextResponse.json(
      { error: 'Failed to generate mock audio' },
      { status: 500 }
    );
  }
} 